import Layout from '../../components/Layout';
import API_KEY from '../API_KEY';

class Detail extends React.Component {

  static async getInitialProps(ctx) {
    const res = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&i=${ctx.query.slug}`);
    const data = await res.json();
    return {
        detail: data,
    };
  }
componentDidMount(){
    // console.log(this.props)
}
  render() {
      const {detail} = this.props
        return (
            <Layout>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-3">
                            <img  className="card-img-top" src={detail.Poster} alt="" /> 
                        </div>
                        
                        <div className="col-lg-8">
                                <h6 className="card-title">{detail.Title}</h6>
                                <div>Year: {detail.Year}, Released: {detail.Released}</div>
                                <div>Genre: {detail.Genre}</div>
                                <div>Director: {detail.Director}</div>
                                <div>Writer: {detail.Writer}</div>
                                <div>Actors: {detail.Actors}</div>
                                <div>Language: {detail.Language}</div>
                                
                                <div className="mt-3">
                                    <b>Awards: {detail.Awards}</b>
                                </div>
                                <h5 className="mt-3">Rating</h5>
                                <div>
                                    {
                                        detail.Ratings.map(item=>(<div>
                                                {item.Source} : {item.Value}
                                        </div>))
                                    }
                                </div>
                                <h5 className="mt-3">BoxOffice</h5>
                                <div>{detail.BoxOffice}</div>
                                
                                <div>Production: {detail.Production}</div>

                        </div>
                    </div>
                </div>
            </Layout>
        );
    }
}

export default Detail
