import Layout from '../components/Layout'
import fetch from 'isomorphic-unfetch';
import API_KEY from './API_KEY';

class Index extends React.Component{
    static async getInitialProps(ctx){
        const res = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&page=1&s=abc`);
        const data = await res.json();
        console.log(data)
        return {
            movieList: data.Search
        };
    }
    handleChange(){
        console.log('etet')
    }
    render(){
        const { movieList } = this.props
        const listing = (movieList.length) && movieList.map(item=>(
            <div className="col-4" key={item.imdbID}>
                <div className="card">
                        <img  className="card-img-top" src={item.Poster} alt="" /> 
                    <div className="card-body">
                        <h3>{item.Title}</h3>
                        <span className="badge bandge-danger">{item.Type}</span>
                    </div>
                </div>
            </div>
        ));

        return(
            <div className="container">
                <input type="text" minLength="3" onChange={() => this.handleChange() } />
                <div className="row">
                    {listing}
                </div>
            </div>
        )
    }
}

