import Layout from '../../components/Layout';
import API_KEY from '../API_KEY';
import Link from 'next/link'


class Page extends React.Component {

  static async getInitialProps(ctx) {
    const res = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&page=1&s=${ctx.query.slug}`);
    const data = await res.json();
    return {
        movieList: data.Search,
    };
  }
  componentDidMount(){
     // console.log(this.props)
  }
  render() {
    const { movieList } = this.props;

          const listing = (movieList.length) && movieList.map((item, index)=>(
            <div className="col-3 mb-3" key={index}>
                <div className="card">
                    <div className="card-header p-0">
                        <img  className="card-img-top" src={item.Poster} alt="" /> 
                    </div>
                    <div className="card-body">
                        <div>
                        <h6 className="card-title">{item.Title}   <span className="badge badge-primary">{item.Type}</span></h6>
                      
                        <Link href="/detail/[slug]" as={`/detail/${item.imdbID}`}>
                          <a className="btn btn-danger btn-sm rounded-pill">View Detail</a>
                        </Link>
                        </div>
                    </div>
                </div>
            </div>
        ));

        return (
            <Layout>
                <div className="container">
                    <div className="row listing">
                          {listing}
                      </div>
                </div>
            </Layout>
        );
    }
}

export default Page
