import  "bootstrap/dist/css/bootstrap.min.css"
import  "../styles/style.scss"
import Link from 'next/link'
import { useState } from 'react';
import { useRouter } from 'next/router';


const Layout = (props) => {
    const router = useRouter();
    const [search, setSearch] = useState(router.query.slug || '');
    const handleChange = (e) =>{
        setSearch(e.target.value)
    }
    return(
    <div className="container">
        <div className="row">
            {search}
            {router.query.slug }
            <div className="input-group py-5">
                <input className="form-control" value={search} onChange={(e)=>handleChange(e)} type="text"  />
               
                <div className="input-group-append">
                    <Link href="/listing/[slug]" as={`/listing/${search}`}>
                        <a className="btn btn-primary">Search</a>
                    </Link>
                </div>
            </div>
            
        </div>
        {
            props.children
        }
    </div>
    )
}

export default Layout
