import  "bootstrap/dist/css/bootstrap.min.css"
import  "../styles/style.scss"
import Link from 'next/link'


const Layout = (props) => {
    const test = () =>{
        console.log(test)
    }
    return(
    <div className="container">
        <div className="row">
            <div className="input-group py-5">
                <input className="form-control" val={props.search} onChange={()=>test()} type="search" value />
               
                <div className="input-group-append">
                    <Link href="/listing/[slug]" as={`/listing/abc`}>
                        <a className="btn btn-primary">Search</a>
                    </Link>
                </div>
            </div>
            
        </div>
        {
            props.children
        }
    </div>
    )
}

Layout.getInitialProps = async function(ctx) {
    console.log(ctxs)
    return {
        search: ctx.query.slug,
    };
};
export default Layout
