import  "bootstrap/dist/css/bootstrap.min.css"
import  "../styles/style.scss"
import Link from 'next/link'
import { useState } from 'react';
import { useRouter } from 'next/router';


const Layout = (props) => {
    const [search, setSearch] = useState('');
    const router = useRouter();

    console.log(router)
    const handleChange = (e) =>{
        setSearch(e.target.value)
    }
    return(
    <div className="container">
        <div className="row">
        <h1>{router.query.slug}</h1>

            <div className="input-group py-5">
                <input className="form-control" value={router.query.slug} onChange={(e)=>handleChange(e)} type="text"  />
               
                <div className="input-group-append">
                    <Link href="/listing/[slug]" as={`/listing/${search}`}>
                        <a className="btn btn-primary">Search</a>
                    </Link>
                </div>
            </div>
            
        </div>
        {
            props.children
        }
    </div>
    )
}

Layout.getInitialProps = async function(ctx) {
    console.log(ctxs)
    return {
        search: ctx.query.slug,
    };
};
export default Layout
