import  "bootstrap/dist/css/bootstrap.min.css"
import  "../styles/style.scss"
import Link from 'next/link'
import Head from 'next/head'
import { useState } from 'react';
import Navbar from '../components/Navbar'
import NProgress from 'nprogress';
NProgress.configure({
    template: "<div class='>Loading...</div>"
  });

Router.onRouteChangeStart = () => {
// console.log('onRouteChnageStart triggered');
NProgress.start();
};

Router.onRouteChangeComplete = () => {
// console.log('onRouteChnageComplete triggered');
NProgress.done();
};

Router.onRouteChangeError = () => {
// console.log('onRouteChnageError triggered');
NProgress.done();
};
  

const Layout = (props) => {
    const [search, setSearch] = useState('');
    const handleChange = (e) =>{
        setSearch(e.target.value)
    }
    return(
    <div>
        <Head>
            <title>React + Next.js</title>
        </Head>
        <Navbar />
        <div className="container">
            <div className="row">
                <div className="col-12 py-4">
                    <h5>Search your favourite movie</h5>
                    <div className="card shadow-sm border-light">
                        <div className="card-body p-lg-4">
                            <form>
                                <div className="input-group ">
                                    <input className="form-control" placeholder="Enter text to Search" value={search} onChange={(e)=>handleChange(e)} type="text"  />
                                
                                    <div className="input-group-append">
                                        <Link href="/listing/[slug]" as={`/listing/${search}`}>
                                            <button type="submit" className="btn btn-danger">Search</button>
                                        </Link>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        {
            props.children
        }
    </div>
    )
}

export default Layout
