import Layout from '../../components/Layout';
import API_KEY from '../API_KEY';
import Link from 'next/link'


class Page extends React.Component {

  static async getInitialProps(ctx) {
    const res = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&page=1&s=${ctx.query.slug}`);
    const data = await res.json();
    console.log(data)
    return {
        movieList: data.Search,
    };
  }
componentDidMount(){
    console.log(this.props)
}
  render() {
    const { movieList } = this.props;

          const listing = (movieList.length) && movieList.map((item, index)=>(
            <div className="col-3" key={index}>
                <div className="card">
                        <img  className="card-img-top" src={item.Poster} alt="" /> 
                    <div className="card-body">
                        <h6 className="card-title">{item.Title}</h6>
                        <span className="badge badge-danger">{item.Type}</span>
                        {item.imdbID}
                        <Link href="/detail/[slug]" as={`/detail/${item.imdbID}`}>
                          <a>View Detail</a>
                        </Link>
                    </div>
                </div>
            </div>
        ));

        return (
            
            <Layout>
            
            <div className="container">
                <input type="text" minLength="3" />
                <div className="row">
                      {listing}
                  </div>
            </div>
            </Layout>
        );
    }
}

export default Page
