import Layout from '../../components/Layout'
import fetch from 'isomorphic-unfetch';
import API_KEY from '../API_KEY';

class Index extends React.Component{
    state = {
        search:"Abc",
        movieList:[],
        isLoading:true
        
    }
    static async getInitialProps(something) {
        console.log(something)
    }
  componentDidMount() {
    console.log(this.props)
    this.getMovieList()
  }

    // componentDidMount(){
    //     this.getMovieList()
    // }
    async getMovieList(){
        const res = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&page=1&s=${this.state.search}`);
        const data = await res.json();
        this.setState({
            movieList: (data.Search)?data.Search :[],
            isLoading:false
        })
        
    }
    handleChange = (e) =>{
        const val = e.target.value
        this.setState({
            isLoading:true
        })
        this.setState(state=>{
            if(this.state.search.length >=3)
            this.getMovieList()
            return {
                search: val
            }
        })
       
    }
    render(){
        const { movieList } = this.state;
        const { search } = this.state;
        const { isLoading } = this.state;

        const listing = (movieList.length) && movieList.map((item, index)=>(
            <div className="col-3" key={index}>
                <div className="card">
                        <img  className="card-img-top" src={item.Poster} alt="" /> 
                    <div className="card-body">
                        <h6 className="card-title">{item.Title}</h6>
                        <span className="badge badge-danger">{item.Type}</span>
                        <a href="/" className="btn btn-primary">View Details</a>

                    </div>
                </div>
            </div>
        ));

        return(
            <Layout>
            
            <div className="container">
                <input type="text" minLength="3" value={search} onChange={(e) => this.handleChange(e) } />

                {
                isLoading ? (<div>Loading</div>) :
                (<div className="row">
                    {listing}
                </div>)
                }
            </div>
            </Layout>
        )
    }
}

