import Layout from '../components/Layout'
import fetch from 'isomorphic-unfetch';
import API_KEY from './API_KEY';


class Index extends React.Component{
    constructor(props){
        super(props)

    }
    render(){
        return(
            <Layout>
                <div className="container">
                    <div className="row">
                    {
                        (this.props.movieList.length) && this.props.movieList.map(item=>(
                            <div className="col-4" key={item.imdbID}>
                                <div className="card">
                                        <img  className="card-img-top" src={item.Poster} alt="" />
                                    <div className="card-body">
                                        <h3>{item.Title}</h3>
                                        <span className="badge bandge-danger">{item.Type}</span>
                                    </div>
                                </div>
                            </div>
                        ))
                    }
                    </div>
                    <input className="form-control" type="search" />
                </div>
            </Layout>
        )
    }
    getInitialProps = async function(){
        const res = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&page=1&s=abc`);
        const data = await res.json();
        console.log(data)
        return {
            movieList: data.Search
        };
    }
}
