import Layout from '../../components/Layout';
import API_KEY from '../API_KEY';

class Detail extends React.Component {

  static async getInitialProps(ctx) {
    const res = await fetch(`http://www.omdbapi.com/?apikey=${API_KEY}&i=${ctx.query.slug}`);
    const data = await res.json();
    console.log(data)
    return {
        detail: data,
    };
  }
componentDidMount(){
    console.log(this.props)
}
  render() {
      const {detail} = this.props
        return (
            <Layout>
                <div className="container">
                    <div className="row">
                        <div className="card">
                            <img  className="card-img-top" src={detail.Poster} alt="" /> 
                            <div className="card-body">
                                <h6 className="card-title">{detail.Title}</h6>
                                <span className="badge badge-danger">{detail.Type}</span>
                                {detail.imdbID}
                            </div>
                        </div>
                    </div>
                </div>
            </Layout>
        );
    }
}

export default Detail
